import axios from 'axios';
import { useAuthStore } from '@/store/auth';

const api = axios.create({
  baseURL: "http://127.0.0.1:5000", // Ensure backend URL matches
  withCredentials: true, // Allows cookies and auth headers
  headers: {
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*", // Allows all origins (for development)
  },
});


api.interceptors.request.use((config) => {
  const authStore = useAuthStore();  // Initialize Pinia store
  const token = authStore.token;  // Get token from Pinia store
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export default api;
